import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";
import PropTypes from "prop-types";
import PlayerDisplay from "./PlayerDisplay";

const GolferDataPage = () => {
  const [golfers, setGolfers] = React.useState([]);
  const [current, setCurrent] = React.useState("Jordan Spieth");
  console.log(current);

  useEffect(() => {
    const fetchData = () => {
      fetch("/api/golfers")
        .then(response => {
          if (!response.ok) {
          //alert("E");
          throw new Error(response.status_text);
        }
        //          alert("T");
          return response.json();
      })
        .then(data => {
          setGolfers(data);
        })
        .catch(err => console.log(err)); // eslint-disable-line no-console
      };
      fetchData();
    }, []);

  const htmlArray = [];
  let currGolfer = null;
  golfers.forEach((golfer) => {
    if (golfer.name === current) {
      currGolfer = golfer;
    }
    htmlArray.add((
      <option key={golfer.name} value={golfer.name}>{golfer.name}</option>
  ));
});

  if (currGolfer) {
    return (
      <div>
        <Row>
          <Container fluid="xl">
            <Row>
              <label>Select a player here:</label>
            </Row>
            <Row>
              <select value={current} onChange={(event) => setCurrent(event.target.value)}>
                {htmlArray}
              </select>
            </Row>
          </Container>
        </Row>
        <Row>
          <PlayerDisplay player={current}></PlayerDisplay>
        </Row>
      </div>
    );
  }
  else {
    return (
      <div>
      </div>

    );
  }





};

export default GolferDataPage;
