import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";
import PropTypes from "prop-types";
const ExcelJS = require('exceljs');

const PlayerName = styled.h3`
  text-align: center;
`;
const PlayerMetrics = styled.div`
  text-align: center;
`;

const PlayerDisplay = ( { player }) => {
  const [results, setResults] = React.useState([]);
  useEffect(() => {
    const fetchData = () => {
      fetch(`/api/results/${player.id}/`)
        .then(response => {
          if (!response.ok) {
          //alert("E");
          throw new Error(response.status_text);
        }
        //          alert("T");
          return response.json();
      })
        .then(data => {
          setResults(data);
        })
        .catch(err => console.log(err)); // eslint-disable-line no-console
      };
      fetchData();
    }, []);


  console.log(player);
  console.log(player.name);
  console.log(results)

  if (results.length === 0) {
    return (
          <div>
            <Container fluid>
              <PlayerName>{player.name}</PlayerName>
              <PlayerMetrics>Height: {player.height} - Weight: {player.weight}</PlayerMetrics>
              <PlayerMetrics>Age: {player.age}</PlayerMetrics>
              <PlayerMetrics>Hometown: {player.hometown}</PlayerMetrics>
              <PlayerMetrics>---------------------------------------</PlayerMetrics>
              <PlayerMetrics>Shots Gained Off-The-Tee: {results[0].sgOTT}</PlayerMetrics>
            </Container>
          </div>

        );
      }

  return (
    <div>
      <Container fluid>
        <PlayerName>{player.name}</PlayerName>
        <PlayerMetrics>Height: {player.height} - Weight: {player.weight}</PlayerMetrics>
        <PlayerMetrics>Age: {player.age}</PlayerMetrics>
        <PlayerMetrics>Hometown: {player.hometown}</PlayerMetrics>

      </Container>
    </div>

  );

};

export default PlayerDisplay;
