import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";
import PropTypes from "prop-types";
import CourseView from "./CourseView";

const Title = styled.h2`
  text-align: center;
  padding: 1rem;
`;

const CourseStatsDisplay = () => {
  const [stat, setStat] = React.useState(null);
  const [courses, setCourses] = React.useState([]);
  const [currCourse, setCurrCourse] = React.useState(null);

  useEffect(() => {
      const fetchCourses = () => {
        fetch("/api/courses")
          .then(response => {
            if (!response.ok) {
            //alert("E");
            throw new Error(response.status_text);
          }
          //          alert("T");
            return response.json();
        })
          .then(data => {
            setCourses(data);
          })
          .catch(err => console.log(err)); // eslint-disable-line no-console
      };
      fetchCourses();
    }, []);

    var htmlArray = [];
    courses.forEach((course) => {
      htmlArray.push((
      <Row key={course.name} >
        <Col>
          <CourseView course={course}></CourseView>
        </Col>
      </Row>

    ));
    });

    return (
      <div>
        <Title>Data for Each Course</Title>
        <Container>
          {htmlArray}
        </Container>
      </div>

    );


};

export default CourseStatsDisplay;
