import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";
import PropTypes from "prop-types";
let fieldReceive = require('./WeekGolf.js');

let field = fieldReceive.field;

const MatchupDisplay = () => {
  const [golfers, setGolfers] = React.useState([]);

  useEffect(() => {
      const fetchGolfers = () => {
        fetch("/api/golfers")
          .then(response => {
            if (!response.ok) {
            //alert("E");
            throw new Error(response.status_text);
          }
          //          alert("T");
            return response.json();
        })
          .then(data => {
            const forWeek = data.filter((golfer) => golfer.name in field);
            setGolfers(forWeek);
          })
          .catch(err => console.log(err)); // eslint-disable-line no-console
      };

      fetchGolfers();

    }, []);


  console.log(golfers);

  return (
    <div>
      <ul>{golfers}</ul>
    </div>
  );

};

export default MatchupDisplay;
