import React, { useState, useEffect } from "react";
import "./App.css";

import styled from "styled-components";
import { Container, Button, Row, Col } from "reactstrap";

const Title = styled.h1`
  text-align: center;
  padding: 1rem;
  background-color: #a0000f;
  color: white;
`;

const ButtonBar = styled.div`
  padding: 3rem 1;
  margin-bottom: 0.5rem;
`;

const Message = styled.div`
  padding: 70px 0;
  text-align: center;
  /*background-image: url('Jefferson.png')*/
`;

const App = () => {
  const [view, setView] = React.useState("home");

  const offerredSports = ["MLB", "NFL", "PGA"];
  const htmlArray = offerredSports.map(sport => (
    <Col key={sport} xs="4">
      <Button
        style={{ backgroundColor: "#0000ff", display: "flex", justifyContent: "center"}}
        onClick={() => {
          setView(sport)
        }}
      >{sport}</Button>
    </Col>
  ));


  if (view === "home") {
    return (
      <div>
        <Title>SRP Picks</Title>
        <Container fluid="xl">
          <Row>
            {htmlArray}
          </Row>
        </Container>
        <Message>Welcome to the big time. Providing sport projections, research, data, and picks since 2018.</Message>
      </div>
    )


  }
  else if (view==="MLB") {
    return (
      <div>MLB</div>
    );

  }
  else if (view==="NFL") {
    return (
      <div>NFL</div>
    );

  }
  else if (view==="PGA") {
    return (
      <div>
        <Title>SRP Picks</Title>
        <Container fluid="xl">
          <Row>
            {htmlArray}
          </Row>
        </Container>
        <PGAPage></PGAPage>
      </div>
    );

  }
  else {
    return (
      <div>None</div>
    );
  }




};

export default App;
