import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button } from "reactstrap";
import PropTypes from "prop-types";

const Title = styled.h2`
  text-align: center;
  padding: 1rem;
`;

const PGAPage = () => {
  const [mode, setMode] = React.useState("main");
  const linksList = ["Player by Player Data", "SG Matchups", "Course History Finder", "Picks"]

  const htmlArray = linksList.map(situation => (
    <Col key={situation} xs="3">
      <Button
        style={{ backgroundColor: "#0000ff", display: "flex", justifyContent: "center"}}
        onClick={() => {
          setMode(situation);
        }}
      >{situation}</Button>
    </Col>
  ));

  return (
    <div>
      <Title>PGA</Title>
      <Container fluid="sm">
        <Row>
          {htmlArray}
        </Row>
      </Container>
    </div>

  );



};

export default PGAPage;
