import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";
import PropTypes from "prop-types";

function Average(arrayResults) {
  var totfairwayAttempt = 0.0;
  var totfairwayHit = 0.0;
  var totfwAtt = 0.0;
  var totfwProx = 0.0;
  var totgdAttempt = 0.0;
  var totgdHit = 0.0;
  var totgir75Attempt = 0.0;
  var totgir75Hit = 0.0;
  var totgir100Attempt = 0.0;
  var totgir100Hit = 0.0;
  var totgir125Attempt = 0.0;
  var totgir125Hit = 0.0;
  var totgir150Attempt = 0.0;
  var totgir150Hit = 0.0;
  var totgir175Attempt = 0.0;
  var totgir175Hit = 0.0;
  var totgir200Attempt = 0.0;
  var totgir200Hit = 0.0;
  var totonePutt = 0.0;
  var totputtAtt = 0.0;
  var totroAtt = 0.0;
  var totroProx = 0.0;
  var totrounds = 0.0;
  var totsandAtt = 0.0;
  var totsandSave = 0.0;
  var totscramblingAtt = 0.0;
  var totscramblingSave = 0.0;
  var totsgAPP = 0.0;
  var totsgArg = 0.0;
  var totsgOTT = 0.0;
  var totsgPutt = 0.0;
  var totthreePutt = 0.0;
  var tottotDrives = 0.0;

  arrayResults.forEach(object => {
    if (object.fairwayAttempt <= 70) {
      totfairwayAttempt += object.fairwayAttempt;
      totfairwayHit += object.fairwayHit;
    }

    if (object.fwAtt <= 70) {
      totfwAtt += object.fwAtt;
      totfwProx += object.fwProx;
    }

    if (object.gdAttempt <= 70) {
      totgdAttempt += object.gdAttempt;
      totgdHit += object.gdHit;
    }

    totgir75Attempt += object.gir75Attempt;
    totgir75Hit += object.gir75Hit;
    totgir100Attempt += object.gir100Attempt;
    totgir100Hit += object.gir100Hit;
    totgir125Attempt += object.gir125Attempt;
    totgir125Hit += object.gir125Hit;
    totgir150Attempt += object.gir150Attempt;
    totgir150Hit += object.gir150Hit;
    totgir175Attempt += object.gir175Attempt;
    totgir175Hit += object.gir175Hit;
    totgir200Attempt += object.gir200Attempt;
    totgir200Hit += object.gir200Hit;
    totonePutt += object.onePutt;
    if (totputtAtt <= 38) {
      totputtAtt += object.puttAtt;
    }


    if (object.roAtt <= 70) {
      totroAtt += object.roAtt;
      totroProx += object.roProx;
    }

    totrounds += object.rounds;

    if (object.sandAtt <= 25) {
      totsandAtt += object.sandAtt;
      totsandSave += object.sandSave;
    }

    if (object.scramblingAtt <= 25) {
      totscramblingAtt += object.scramblingAtt;
      totscramblingSave += object.scramblingSave;
    }

    totsgAPP += object.sgAPP;
    totsgArg += object.sgArg;
    totsgOTT += object.sgOTT;
    totsgPutt += object.sgPutt;
    totthreePutt += object.threePutt;
    tottotDrives += object.totDrives;
  });

  const result = {
    fairwayAttempt: totfairwayAttempt,
    fairwayHit: totfairwayHit,
    fwAtt: totfwAtt,
    fwProx: totfwProx,
    gdAttempt: totgdAttempt,
    gdHit: totgdHit,
    gir75Attempt: totgir75Attempt,
    gir75Hit: totgir75Hit,
    gir100Attempt: totgir100Attempt,
    gir100Hit: totgir100Hit,
    gir125Attempt: totgir125Attempt,
    gir125Hit: totgir125Hit,
    gir150Attempt: totgir150Attempt,
    gir150Hit: totgir150Hit,
    gir175Attempt: totgir175Attempt,
    gir175Hit: totgir175Hit,
    gir200Attempt: totgir200Attempt,
    gir200Hit: totgir200Hit,
    onePutt: totonePutt,
    puttAtt: totputtAtt,
    roAtt: totroAtt,
    roProx: totroProx,
    rounds: totrounds,
    sandAtt: totsandAtt,
    sandSave: totsandSave,
    scramblingAtt: totscramblingAtt,
    scramblingSave: totscramblingSave,
    sgAPP: totsgAPP,
    sgArg: totsgArg,
    sgOTT: totsgOTT,
    sgPutt: totsgPutt,
    threePutt: totthreePutt,
    totDrives: tottotDrives
  };

  return result;

};


const FieldHolder = ( { golfers, stat, period, courseId, results } ) => {

  let filteredResults;
  if (period === "This Course") {
    filteredResults = results.filter((result) => result.courseId === courseId);
  }
  else if (period === "This Season") {
    filteredResults = results.filter((result) => result.year === 2020);
  }
  else {
    filteredResults = results;
  }

  let htmlArray = [];
  let i;
  let layout = [];

  for (i = 0; i < golfers.length; i++) {
    const value = Average(golfers[i], results, stat);
    layout = Insert(layout, value);
  }


  layout.forEach((item, index) => {
    htmlArray.push((
      <Row>
        <Col>
          <label>{index + 1}</label>
        </Col>
        <Col>
          <label>{item.name}</label>
        </Col>
        <Col>
          <label>{item.stat}</label>
        </Col>
      </Row>
    ));

  });

  if (stat === "SG OTT") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.sgOTT/results.length}</label>
      </Col>
    );
  }
  else if (stat === "SG APP") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.sgAPP/results.length}</label>
      </Col>
    );

  }
  else if (stat === "SG ARG") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.sgArg/results.length}</label>
      </Col>
    );
  }
  else if (stat === "SG PUTT") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.sgPutt/results.length}</label>
      </Col>
    );

  }
  else if (stat === "Driving Accuracy") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.fairwayHit/desiredOutput.fairwayAttempt}</label>
      </Col>
    );

  }
  else if (stat === "Good Drive Percentage") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.gdHit/desiredOutput.gdAttempt}</label>
      </Col>
    );

  }
  else if (stat === "Approaches +200 GIR") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.gir200Hit/desiredOutput.gir200Attempt}</label>
      </Col>
    );
  }
  else if (stat === "Approaches 175-200 GIR") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.gir175Hit/desiredOutput.gir175Attempt}</label>
      </Col>
    );

  }
  else if (stat === "Approaches 150-175 GIR") {
    bottomVal = (
    <Col>
      <label>{desiredOutput.gir150Hit/desiredOutput.gir150Attempt}</label>
    </Col>
  );

  }
  else if (stat === "Approaches 125-150 GIR") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.gir125Hit/desiredOutput.gir125Attempt}</label>
      </Col>
    );

  }
  else if (stat === "Approaches 100-125 GIR") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.gir100Hit/desiredOutput.gir100Attempt}</label>
      </Col>
    );

  }
  else if (stat === "Approaches -100 GIR") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.gir75Hit/desiredOutput.gir75Attempt}</label>
      </Col>
    );

  }
  else if (stat === "Sand Save Percentage") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.sandSave/desiredOutput.sandAtt}</label>
      </Col>
    );
  }
  else if (stat === "Scrambling Capability") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.scramblingSave/desiredOutput.scramblingAtt}</label>
      </Col>
    );
  }
  else if (stat === "One Putt-Three Putt") {
    bottomVal = (
      <Col>
        <label>{desiredOutput.onePutt/(desiredOutput.onePutt + desiredOutput.threePutt).toString() + "/" + desiredOutput.threePutt/(desiredOutput.onePutt + desiredOutput.threePutt).toString()}</label>
      </Col>
    );
  }


  return (
    <div>
      <Row>
        <Col>
          <label>{golfer.name}</label>
        </Col>
        {bottomVal}
      </Row>
    </div>
  );


};

export default FieldHolder;
