import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";
import PropTypes from "prop-types";
const ExcelJS = require('exceljs');

const PlayerName = styled.h3`
  text-align: center;
`;
const PlayerMetrics = styled.div`
  text-align: center;
`;


function Average(arrayResults) {
  var totfairwayAttempt = 0.0;
  var totfairwayHit = 0.0;
  var totfwAtt = 0.0;
  var totfwProx = 0.0;
  var totgdAttempt = 0.0;
  var totgdHit = 0.0;
  var totgir75Attempt = 0.0;
  var totgir75Hit = 0.0;
  var totgir100Attempt = 0.0;
  var totgir100Hit = 0.0;
  var totgir125Attempt = 0.0;
  var totgir125Hit = 0.0;
  var totgir150Attempt = 0.0;
  var totgir150Hit = 0.0;
  var totgir175Attempt = 0.0;
  var totgir175Hit = 0.0;
  var totgir200Attempt = 0.0;
  var totgir200Hit = 0.0;
  var totonePutt = 0.0;
  var totputtAtt = 0.0;
  var totroAtt = 0.0;
  var totroProx = 0.0;
  var totrounds = 0.0;
  var totsandAtt = 0.0;
  var totsandSave = 0.0;
  var totscramblingAtt = 0.0;
  var totscramblingSave = 0.0;
  var totsgAPP = 0.0;
  var totsgArg = 0.0;
  var totsgOTT = 0.0;
  var totsgPutt = 0.0;
  var totthreePutt = 0.0;
  var tottotDrives = 0.0;

  result = arrayResults.forEach(object => {
    totfairwayAttempt += object.fairwayAttempt;
    var totfairwayHit = 0.0;
    var totfwAtt = 0.0;
    var totfwProx = 0.0;
    var totgdAttempt = 0.0;
    var totgdHit = 0.0;
    var totgir75Attempt = 0.0;
    var totgir75Hit = 0.0;
    var totgir100Attempt = 0.0;
    var totgir100Hit = 0.0;
    var totgir125Attempt = 0.0;
    var totgir125Hit = 0.0;
    var totgir150Attempt = 0.0;
    var totgir150Hit = 0.0;
    var totgir175Attempt = 0.0;
    var totgir175Hit = 0.0;
    var totgir200Attempt = 0.0;
    var totgir200Hit = 0.0;
    var totonePutt = 0.0;
    var totputtAtt = 0.0;
    var totroAtt = 0.0;
    var totroProx = 0.0;
    var totrounds = 0.0;
    var totsandAtt = 0.0;
    var totsandSave = 0.0;
    var totscramblingAtt = 0.0;
    var totscramblingSave = 0.0;
    var totsgAPP = 0.0;
    var totsgArg = 0.0;
    var totsgOTT = 0.0;
    var totsgPutt = 0.0;
    var totthreePutt = 0.0;
    var tottotDrives = 0.0;
  });

};


const PlayerDisplay = ( { player }) => {
  const [results, setResults] = React.useState([]);
  useEffect(() => {
    const fetchData = () => {
      fetch(`/api/results/${player.id}/`)
        .then(response => {
          if (!response.ok) {
          //alert("E");
          throw new Error(response.status_text);
        }
        //          alert("T");
          return response.json();
      })
        .then(data => {
          setResults(data);
        })
        .catch(err => console.log(err)); // eslint-disable-line no-console
      };
      fetchData();
    }, []);

  if (results.length !== 0) {


    return (
          <div>
            <Container fluid>
              <PlayerName>{player.name}</PlayerName>
              <PlayerMetrics>Height: {player.height} - Weight: {player.weight}</PlayerMetrics>
              <PlayerMetrics>Age: {player.age}</PlayerMetrics>
              <PlayerMetrics>Hometown: {player.hometown}</PlayerMetrics>
              <PlayerMetrics>---------------------------------------</PlayerMetrics>
              <PlayerMetrics>Shots Gained Off-The-Tee: {results[0].sgOTT}</PlayerMetrics>

            </Container>
          </div>

        );
    }

  return (
    <div>
      <Container fluid>
        <PlayerName>{player.name}</PlayerName>
        <PlayerMetrics>Height: {player.height} - Weight: {player.weight}</PlayerMetrics>
        <PlayerMetrics>Age: {player.age}</PlayerMetrics>
        <PlayerMetrics>Hometown: {player.hometown}</PlayerMetrics>

      </Container>
    </div>

  );

};

export default PlayerDisplay;
