import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { Row, Col, Button, Container } from "reactstrap";
import PropTypes from "prop-types";
import CourseView from "./CourseView";

const Title = styled.h2`
  text-align: center;
  padding: 1rem;
`;

const CourseStatsDisplay = () => {
  const [stat, setStat] = React.useState(null);
  const [courses, setCourses] = React.useState([]);

  useEffect(() => {
      const fetchCourses = () => {
        fetch("/api/courses")
          .then(response => {
            if (!response.ok) {
            //alert("E");
            throw new Error(response.status_text);
          }
          //          alert("T");
            return response.json();
        })
          .then(data => {
            setCourses(data);
          })
          .catch(err => console.log(err)); // eslint-disable-line no-console
      };
      fetchCourses();
    }, []);

    var htmlArray = [];
    courses.forEach((course) => {
      htmlArray.push((
      <Row key={course.name} >
        <CourseView course={course}></CourseView>
      </Row>

    ));
    });

    const dropBox = (
      <select value={stat} onChange={(event) => setStat(event.target.value)}>
        <option key="Driving Accuracy" value="Driving Accuracy">Driving Accuracy</option>
        <option key="Good Drive Percentage" value="Good Drive Percentage">Good Drive Percentage</option>
        <option key="Approaches +200 Percentage" value="Approaches +200 Percentage">Approaches +200 Percentage</option>
        <option key="Approaches +175 Percentage" value="Approaches +175 Percentage">Approaches +175 Percentage</option>
        <option key="Approaches +150 Percentage" value="Approaches +150 Percentage">Approaches +150 Percentage</option>
        <option key="Approaches +125 Percentage" value="Approaches +125 Percentage">Approaches +125 Percentage</option>
        <option key="Approaches +100 Percentage" value="Approaches +100 Percentage">Approaches +100 Percentage</option>
        <option key="Approaches +75 Percentage" value="Approaches +75 Percentage">Approaches +75 Percentage</option>
        <option key="Sand Attempts per Round" value="Sand Attempts per Round">Sand Attempts per Round</option>
        <option key="Scrambling Capability" value="Scrambling Capability">Scrambling Capability</option>
        <option key="One Putt-Three Putt" value= "One Putt-Three Putt">One Putt-Three Putt</option>
      </select>


    );

    return (
      <div>
        <Title>Data for Each Course</Title>
        <Container>
          <Row>
            <Col>
              {dropBox}
            </Col>
          </Row>
          {htmlArray}
        </Container>
      </div>

    );


};

export default CourseStatsDisplay;
